import React, { Component } from 'react';
import { TextField, withStyles, makeStyles, Grid, Container, Button, FormControl, InputLabel, select, MenuItem } from '@material-ui/core'

const styles = makeStyles(theme => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 500,
    },
}));

class edit extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            title: '',
            body: '',
            userid: 0,
            users: [],
            id: this.props.location.id,
        };
    }

    changeHandler = (event) => {
        this.setState({[event.target.name]: event.target.value})
    }

    editPost = () => {

        fetch(`https://jsonplaceholder.typicode.com/posts/${this.state.id}`, {
            method: 'PUT',
            body: JSON.stringify({
                title: this.state.title,
                body: this.state.body,
                userId: this.state.userid
            }),
            headers: {
                "Content-type": "application/json; charset-UTF-8"
            }
        })
            .then(response => response.json())
            .then(json => console.log(json))

    }

    componentDidMount() {
        fetch("https://jsonplaceholder.typicode.com/users")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        users:result,
                        title:result.title,
                        body:result.body,
                        userid:result.userId,
                    });
                },
                (error) => {
                    console.log(error)
                }
            )

        fetch(`https://jsonplaceholder.typicode.com/posts/${this.state.id}`)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        items:result,
                        title:result.title,
                        body:result.body,
                        userid:result.userId,
                    });
                },
                (error) => {
                    console.log(error)
                }
            )
    }

    render() {
        const { classes } = this.props;
        return (
            <Container fixed>
                <Grid container spacing={3}>
                    <form className={classes.container} noValidate autoComplete="off">
                        <div>
                            <TextField
                                required
                                id="title"
                                name="title"
                                className={classes.textField}
                                style={{ width: 500 }}
                                margin="normal"
                                onChange={this.changeHandler}
                                value={this.state.title}
                            />
                        </div>
                        <div>
                            <TextField
                                required
                                id="body"
                                name="body"
                                className={classes.textField}
                                style={{ width: 500 }}
                                margin="normal"
                                onChange={this.changeHandler}
                                value={this.state.body}
                            />
                        </div>
                        <div>
                            <FormControl style={{ marginBottom: 10 }}>
                                <InputLabel shrink id="label-demi">

                                </InputLabel>
                                <select
                                    labelId="label-demi"
                                    id="label-demi-id"
                                    onChange={this.changeHandler}
                                    displayEmpty
                                    name="userid"
                                    style={{ width: 500, marginBottom: 10 }}
                                    value=""
                                >
                                    {this.state.users.map((item,i) => (
                                        (item.id===this.state.userid)
                                            ?
                                            <MenuItem key={i} value="" selected>{item.name}
                                            </MenuItem>
                                            :
                                            <MenuItem key={i} value={item.id}>{item.name}
                                            </MenuItem>
                                    ))}
                                </select>

                            </FormControl>
                        </div>
                        <Button variant="contained" color="primary" onClick={this.editPost}>
                            Submit
                        </Button>
                    </form>
                </Grid>
            </Container>
        );
    }
}

export default withStyles(styles)(edit)
